<?phpnamespace App;use Antonrom\ModelChangesHistory\Traits\HasChangesHistory;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\Relations\BelongsTo;use Illuminate\Database\Eloquent\SoftDeletes;class Answer extends Model{    use SoftDeletes, HasChangesHistory;    /**     * @var string     */    protected $table = 'answers';    /**     * @var string[]     */    protected $fillable = [        'detail',        'user_id',        'question_id',    ];    /**     * @return BelongsTo     */    public function user(): BelongsTo    {        return $this->belongsTo(User::class);    }    /**     * @return BelongsTo     */    public function question(): BelongsTo    {        return $this->belongsTo(Question::class);    }}