<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\Relations\HasMany;use Illuminate\Database\Eloquent\SoftDeletes;class DocumentType extends Model{    use SoftDeletes;    /**     * @var string     */    protected $table = 'document_types';    /**     * @var string[]     */    protected $fillable = [        'name',        'code',        'length',        'is_number',        'is_exact',    ];    /**     * @return HasMany     */    public function students(): HasMany    {        return $this->hasMany(Student::class);    }    /**     * @return HasMany     */    public function teachers(): HasMany    {        return $this->hasMany(Teacher::class);    }}