<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\Relations\BelongsTo;use Illuminate\Database\Eloquent\Relations\HasMany;use Illuminate\Database\Eloquent\SoftDeletes;class Question extends Model{    use SoftDeletes;    /**     * @var string     */    protected $table = 'questions';    /**     * @var string[]     */    protected $fillable = [        'code',        'title',        'detail',        'remark',        'maxwrd',        'maxgrp',        'section_id',    ];    /**     * @return BelongsTo     */    public function section(): BelongsTo    {        return $this->belongsTo(Section::class);    }    /**     * @return HasMany     */    public function answers(): HasMany    {        return $this->hasMany(Answer::class);    }}