<?phpnamespace App;use Antonrom\ModelChangesHistory\Traits\HasChangesHistory;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\Relations\BelongsTo;use Illuminate\Database\Eloquent\SoftDeletes;class Review extends Model{    use SoftDeletes, HasChangesHistory;    /**     * @var string     */    protected $table = 'reviews';    /**     * @var string[]     */    protected $fillable = [        'score1',        'score2',        'score3',        'score4',        'feedback',        'user_id',        'jury_id',    ];    /**     * @return BelongsTo     */    public function team(): BelongsTo    {        return $this->belongsTo(User::class, 'user_id');    }    /**     * @return BelongsTo     */    public function jury(): BelongsTo    {        return $this->belongsTo(User::class, 'jury_id');    }}