<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\Relations\BelongsTo;use Illuminate\Database\Eloquent\Relations\HasMany;use Illuminate\Database\Eloquent\SoftDeletes;class Section extends Model{    use SoftDeletes;    /**     * @var string     */    protected $table = 'sections';    /**     * @var string[]     */    protected $fillable = [        'code',        'title',        'description',        'edition_id',    ];    /**     * @return BelongsTo     */    public function edition(): BelongsTo    {        return $this->belongsTo(Edition::class);    }    /**     * @return HasMany     */    public function questions(): HasMany    {        return $this->hasMany(Question::class);    }}